      </div>-->
      <div class="rg_center">
        <div class="rg_form">
          <div class="rg_left">
<!--            <p>新用户注册</p>-->
<!--            <p>USER REGISTER</p>-->
          </div>
          <el-form :model="ruleForm" status-icon :rules="rules" ref="ruleForm" label-width="100px">
            <el-form-item class="td_left" label="手机号" prop="phone">
              <el-input class="td_right" v-model.number="ruleForm.phone" clearable/>
            </el-form-item>
            <el-form-item class="td_left" label="身份证号" prop="idCard">
              <el-input class="td_right" v-model.id="ruleForm.idCard" clearable/>
            </el-form-item>
            <el-form-item class="td_left" label="邮箱" prop="email">
              <el-input class="td_right" v-model.email="ruleForm.email" clearable></el-input>
