              <el-input class="td_right" v-model="ruleForm.name" clearable></el-input>
            </el-form-item>
            <el-form-item class="td_left" label="密码" prop="password">
              <el-input class="td_right" v-model="ruleForm.password" autocomplete="off" show-password id="password" clearable></el-input>
            </el-form-item>
            <el-form-item class="td_left" label="出生日期" prop="brithday">
              <el-date-picker class="td_right" v-model="brithday"></el-date-picker>
            </el-form-item>
<el-form-item>
  <el-button class="btn_sub" type="primary" @click="regist('ruleForm')"> 注册</el-button>
